import React, { useState } from "react";
import wineBottle from "../WINESRCS/winebottlesmaller.svg";
import axios from "axios";

function LabelMaker() {
  const [formData, setFormData] = useState({
    generatedimg: "",
    style: "",
    labelPrompt: "",
  });

  const [generatedImages, setGeneratedImages] = useState([]);

  const generateImages = async (style) => {
    const response = await axios.post(
      "https://api.openai.com/v1/images/generations",
      {
        prompt: `A detailed lithograph by Aurelien Lefort showcasing an ${style} style natural wine label with esoteric and mystic imagery.`,
        n: 4,
        size: "1024x1024",
      },
      {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`,
        },
      }
    );
    setGeneratedImages(response.data.data);
  };

  // const handleSubmit = (e) => {
  //   e.preventDefault();
  //   const new_label = {
  //     generatedimg: formData["generatedimg"],
  //     style: formData["style"],
  //   };
  //   console.log("New label:", new_label);
  //   fetch("/labels", {
  //     method: "POST",
  //     headers: {
  //       "Content-Type": "application/json",
  //     },
  //     body: JSON.stringify(new_label),
  //   })
  //     .then((res) => res.json())
  //     .then((data) => {
  //       setFormData(data);
  //       // setSuccessMessage("Label Created Successfully");
  //     });
  // };

  const handleSubmitLabel = (e) => {
    e.preventDefault();
    generateImages(formData.labelPrompt);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({ ...prevState, [name]: value }));
  };

  return (
    <div className="relative w-full min-h-screen flex items-center justify-center">
      <div className="w-full max-w-7xl mx-auto flex items-center justify-between">
        <div className="relative w-1/4 h-5/6">
          <div className="absolute top-0 left-0 w-full h-full bg-gray-400 opacity-50 z-0"></div>
          <div className="absolute top-0 left-0 w-full h-full flex justify-center items-center z-10">
            {generatedImages.map((image, index) => (
              <img
                key={index}
                src={image.url}
                alt={`Generated Label ${index + 1}`}
                className="w-full mb-4 object-contain"
              />
            ))}
          </div>
        </div>
        <div className="w-full h-5/6 flex justify-center items-center z-0">
          <img
            src={wineBottle}
            alt="Wine Bottle"
            className="h-full object-contain z-10"
          />
        </div>
        <div className="w-2/4 h-5/6 px-8 py-8 z-0 float-right">
          <div className="w-full max-w-md mx-auto">
            <form
              className="w-full h-full bg-custom-gray border-8 border-custom-black p-6 text-black mb-8"
              onSubmit={handleSubmitLabel}
            >
              <h2 className="text-2xl font-bold text-center text-black mb-8">
                Create Your Label
              </h2>

              <div className="mb-4">
                <label
                  className="block text-black text-sm font-bold mb-2"
                  htmlFor="labelPrompt"
                >
                  Label Prompt
                </label>
                <select
                  className="shadow appearance-none rounded w-full py-2 px-3 text-black leading-tight focus:outline-none focus:shadow-outline bg-rectangle-gray"
                  id="labelPrompt"
                  name="labelPrompt"
                  value={formData.labelPrompt}
                  onChange={handleChange}
                >
                  <option value="">Select a prompt</option>
                  <option value="prompt1">Expressionist</option>
                  <option value="prompt2">Water-Color</option>
                  <option value="prompt3">Abstract</option>
                  <option value="prompt4">Surreal</option>
                  <option value="prompt5">Soviet Propoganda</option>
                </select>
              </div>
              <div className="flex items-center justify-center">
                <button
                  className="bg-rectangle-gray hover:scale-105 text-black font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  type="submit"
                >
                  Create Label
                </button>
              </div>
            </form>
            <form className="w-full h-full bg-custom-gray border-8 border-custom-black p-6 text-black">
              <h2 className="text-2xl font-bold text-center text-black mb-8">
                Create Your Wine
              </h2>

              <div className="mb-4">
                <label
                  className="block text-black text-sm font-bold mb-2"
                  htmlFor="name"
                >
                  Name
                </label>
                <input
                  className="shadow appearance-none rounded w-full py-2 px-3 text-black leading-tight focus:outline-none focus:shadow-outline bg-rectangle-gray"
                  id="name"
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                />
              </div>
              <div className="mb-4">
                <label
                  className="block text-black text-sm font-bold mb-2"
                  htmlFor="style"
                >
                  Style
                </label>
                <input
                  className="shadow appearance-none rounded w-full py-2 px-3 text-black leading-tight focus:outline-none focus:shadow-outline bg-rectangle-gray"
                  id="style"
                  type="text"
                  name="style"
                  value={formData.style}
                  onChange={handleChange}
                />
              </div>
              <div className="mb-4">
                <label
                  className="block text-black text-sm font-bold mb-2"
                  htmlFor="grapes"
                >
                  Grapes
                </label>
                <input
                  className="shadow appearance-none rounded w-full py-2 px-3 text-black leading-tight focus:outline-none focus:shadow-outline bg-rectangle-gray"
                  id="grapes"
                  type="text"
                  name="grapes"
                  value={formData.grapes}
                  onChange={handleChange}
                />
              </div>
              <div className="mb-4">
                <label
                  className="block text-black text-sm font-bold mb-2"
                  htmlFor="year"
                >
                  Year
                </label>
                <input
                  className="shadow appearance-none rounded w-full py-2 px-3 text-black leading-tight focus:outline-none focus:shadow-outline bg-rectangle-gray"
                  id="year"
                  type="text"
                  name="year"
                  value={formData.year}
                  onChange={handleChange}
                />
              </div>
              <div className="flex items-center justify-center">
                <button
                  className="bg-rectangle-gray hover:scale-105 text-black font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                  type="submit"
                >
                  Generate
                </button>
              </div>
            </form>
          </div>
        </div>
        <div
          className="fixed top-0 left-0 w-1/4 h-full bg-rectangle-gray opacity-50 z-0"
          style={{ zIndex: "-1" }}
        ></div>
        <div
          className="fixed top-0 right-0 w-1/4 h-full bg-rectangle-gray opacity-50 z-0"
          style={{ zIndex: "-1" }}
        ></div>
      </div>
    </div>
  );
}

export default LabelMaker;
